INSERT INTO `countries` (`id`, `information`, `useful_information`, `created`, `name`, `visa_info`, `image_file`, `slug`, `keywords`, `title`, `continent_id`) VALUES
(1, 'Görögország', 'Görögország hasznos', '2012-12-21 12:27:57', 'Görögország', 'vízum információ: Görögország', 'gorogo.png', 'Görögország1', 'Görögország', 'Görögország', 1),
(2, 'Deviza előírások: a belföldi és külföldi fizetőeszközök be- és kivitele nem korlátozott.\r\nEgészségügyi előírások: kötelező védőoltás nincs, több helyen a palackozott víz fogyasztása javasolt.\r\nElektromos feszültség: 220 V.\r\nPénznem: euro, 1 EUR = 100 cent. Nemzetközi hitelkártyák széles körben elfogadottak.\r\nIdőjárás: kontinentális', 'Franciaország nemzeti szimbóluma Marianne, a gall-sapkás nőalak, aki egyben a szabadság és a francia forradalom jelképe is. Az előző szimbólum a Gall Kakas volt, a vakmerő bátorság jelképe.\r\n\r\n<p>Szomszédai Belgium, Luxemburg, Németország, Svájc, Olaszország, Monaco, Andorra és Spanyolország. Tengerentúli függőségein keresztül határos Brazíliával, Suriname-mal és a Holland Antillákkal. Franciaországot és Nagy-Britanniát a La Manche-csatorna alatt futó Csalagút köti össze. Franciaország a legnagyobb területű nyugat-európai ország és a második legnagyobb kizárólagos gazdasági övezettel rendelkezik, mely több mint 11 millió négyzetkilométer területű.</p>', '2012-12-19 09:58:54', 'Franciaország', 'vízum információ: Franciaország', 'franciao.png', 'franciaorszag', 'Franciaország', 'Franciaország', 0),
(3, 'országinformáció: Spanyolország', 'A spanyol szó végső eredete a latin HISPANIOLUS, azaz kb. ’hispánka’, amely espaignol alakban került a provanszálba, onnan español írásmóddal a spanyolba, majd a többi (újlatin) nyelvbe. A magyar nyelvben egy északolasz nyelvjárási – s-sel ejtett – spagnol alak honosodott meg.', '2012-12-15 15:52:04', 'Spanyolország', 'vízum információ: Spanyolország', 'franciao.png', 'Spanyolország3', 'Spanyolország', 'Spanyolország', 0),
(4, 'országinformáció: Skócia', 'Skócia (angolul Scotland, skót gaelül Alba) Nyugat-Európában található, Nagy-Britannia második legnagyobb országrésze terület és népesség alapján. A Brit-sziget északi harmadát foglalja el, délről Anglia, keletről az Északi-tenger, északról és nyugatról az Atlanti-óceán határolja, délnyugatról pedig az Északi-csatorna és az Ír-tenger. Mintegy 790 sziget tartozik hozzá.', '2012-12-13 13:35:30', 'Skócia', 'vízum információ: Skócia', '3.jpeg', 'Skócia4', 'Skócia', 'Skócia', 0),
(5, 'A Német Szövetségi Köztársaság (németül Bundesrepublik Deutschland; nemzetközi szerződésekben, okmányokban, hivatalos fórumokon a Németországi Szövetségi Köztársaság megnevezés szerepel hivatalos országnévként) Európa vezető, s a világ negyedik vezető ipari és gazdasági nagyhatalma (az USA, Kína és Japán után), Közép-Európában fekszik.', 'A Német Szövetségi Köztársaság (németül Bundesrepublik Deutschland; nemzetközi szerződésekben, okmányokban, hivatalos fórumokon a Németországi Szövetségi Köztársaság megnevezés szerepel hivatalos országnévként) Európa vezető, s a világ negyedik vezető ipari és gazdasági nagyhatalma (az USA, Kína és Japán után), Közép-Európában fekszik.', '2012-12-15 15:52:11', 'Németország', NULL, 'franciao.png', 'nemetorszag', 'Németország', 'Németország', 0),
(6, 'A Magyarországnál hétszer kisebb területű szigetcsoport 690 szigete és több mint 2000 korallszirtje Floridától délkeletre ívelődik az Atlanti-óceánban.', 'A Magyarországnál hétszer kisebb területű szigetcsoport 690 szigete és több mint 2000 korallszirtje Floridától délkeletre ívelődik az Atlanti-óceánban.', '2012-12-21 12:35:13', 'Bahamák/Bermudák', NULL, 'franciao.png', 'bahamak-bermudak', '', '', 2),
