INSERT INTO `hotels` (`id`, `name`, `star_rating`, `description`, `accommodation`, `price`, `trip_id`, `image_file`) VALUES
(1, 'Catalonia Aragón', 3, 'Az egyszerű, de barátságos szálloda a Sagrada amiliától alig pár lépésre épült. Tömegközlekedéssel vagy gyalogosan a város többi nevezetessége is könnyen megközelíthető. 160 szobája légkondicionált, telefonnal, tv-vel, széffel és hajszárítóval felszerelt. Szolgáltatásai: étterem, bár, parkoló.', 'Teszt hotel 1 ellátása', 'Kétágyas szoba 13 900 Ft/fő/éj reggelivel', 1, ''),
(2, 'A&O Hauptbahnhof', 2, 'A főpályaudvartól 10 perc sétára épült a szálloda. A Hafen-city és a legjelentősebb bevásárlóutcák is gyalogos távolságra vannak. A szobákban wifi, a hallban internetsarok várja a vendégeket. Szolgáltatásai: billiárd, csocsó, Nintendo játékok, bár. Szobái egyszerűen berendezettek.', 'Ellátás', 'Kétágyas szoba 12 200 Ft/fő/éjtől', 2, ''),
(3, 'Commodore', 3, 'A hoteltől néhány perc sétára van a kikötő, a belváros és a Reeperbahn negyed. Szobái barátságosan felszereltek, tv-vel, telefonnal, wifivel berendezettek. Szolgáltatásai: étterem, bár, parkoló.', '', 'Kétágyas szoba 16 700 Ft/fő/éjtől', 2, ''),
(5, 'Második hotel', 2, 'asdasdasd', '', '2322.000 Ft/fő', 18, ''),
(6, 'Hotel', 3, 'Leírás', '', '12400', 17, ''),
(7, 'Catalonia Aragon', 3, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;color:#343434;">Az egyszerű, de barátságos szálloda a Sagrada amiliától alig pár lépésre épült.\r\nTömegközlekedéssel vagy gyalogosan a város többi nevezetessége is könnyen\r\nmegközelíthető. 160 szobája légkondicionált, telefonnal, tv-vel, széffel és\r\nhajszárítóval felszerelt. Szolgáltatásai: étterem, bár, parkoló.<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 13 900 Ft/fő/éj reggelivel', 27, ''),
(8, 'Tryp Apolo', 4, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;">A történelmi belvárosban, a Ramplas környékén áll a hotel. Elegáns\r\nszobái csendesek, kényelmesek, légkondicionáltak; wifi, telefon, minibár, tv,\r\níróasztal, hajszárító, széf áll a vendégek rendelkezésére. Szolgáltatásai:\r\nparkoló, fitneszterem (20 méterre a hoteltől, a szállóvendégeknek ingyenes),\r\ninternetsarok 6 számítógéppel. Étterem, kávézó és bár várja a helyi és\r\nnemzetközi specialitásokkal.<span id="pastemarkerend">&nbsp;</span></span></p>\r\n', '', 'Kétágyas szoba 20 300 Ft/fő/éj reggelivel', 27, ''),
(9, 'Catalonia Plaza Catalunya', 4, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;">Barcelona központjában, a Ramblastól és a Plaza Catalunyától csak pár\r\nperc sétára található szállodának nemcsak a homlokzata különleges, hanem a\r\nnyüzsgő városban a nyugalom szigetét jelentő belső tere is, ahol a medencében\r\nlehet felfrissülni. Az 1881-ben készült épületet igyekeznek eredeti formájában\r\nmegtartani: lenyűgöző többek közt az impozáns lépcsősor, a különleges mennyezet\r\nés a mahagóni ajtók is. A vendégek a szállodalánc többi tagjának a spa- és\r\nwellnessrészlegét is látogathatják.<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 21 500 Ft/fő/éj reggelivel', 27, ''),
(10, 'Melia Barcelona', 5, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;">A város egyik legelegánsabb luxusszállodájának nemcsak elhelyezkedése\r\ntökéletes, de a spa részlege is mesés, éttermének kínálata pedig egészen\r\negyedülálló. Könnyen megközelíthető a történelmi belváros, a látványosságok, a\r\nlegjobb üzletek, éttermek és bárok. Ha fontos Önnek a személyre szabott\r\nkiszolgálás, válassza a „The Level” szobakategóriát és felejthetetlen\r\nélményekben lesz része! 333 szobájában hidromasszázs kád vagy zuhanyzó, LCD tv,\r\nADSL internet és különlegesen finom és kényelmes ágyneműk biztosítják a\r\ntökéletes kikapcsolódást. Éttermei, bárjai, a fitneszterem és a spa-részleg is\r\nigazi különlegességekkel várja.<span id="pastemarkerend">&nbsp;</span></span></p>\r\n', '', 'Kétágyas szoba 22 900 Ft/fő/éj reggelivel', 27, ''),
(11, 'A&O Berlin Mitte', 2, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;">Berlin központjában a Spree folyótól nem messze, az Alexander tér és a\r\nkeleti pályaudar között, üzletek, bárok, éttermek szomszédságában található. A\r\nbárban billiárdozni és csocsózni lehet, a hallban internetsarok várja a\r\nvendégeket. Az egyszerűen berendezett szobákban wifi van.<br>\r\n\r\n<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 11 200 Ft/fő/éjtől', 28, ''),
(12, 'BW Euro Hotel Berlin ***', 3, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;color:#343434;">A modern szálloda központi elhelyezkedésű, az Alexanderplatz és a Brandenburg\r\nkapu is könnyen elérhető metróval. Szobái légkondicionáltak, tv-vel, minibárra,\r\ntelefonnal, hajszárítóval felszereltek, A 6. emeleti „télikertben” szolgálják\r\nfel a reggelit. A szálloda vendégei a szemközti hotelben ingyenesen\r\nhasználhatják a fitnesztermet és a szaunát.<br>\r\n\r\n<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 12 700 Ft/fő/éjtől', 28, ''),
(13, 'Park Inn Berlin Alexanderplatz ****', 4, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;"><br>\r\n\r\nAz 1012 szobás, 37 emeletes szálloda a városközpontban, a legendás\r\nAlexanderplatzon található. Panorámateraszáról az egész városra rálátni, a 37.\r\nemeleten egy kaszinó várja a játékosokat, wellness-részlegén szauna,\r\nfitneszterem, egészségbár és különféle masszázsok segítik a felfrissülést.\r\nSzobái a kategóriának megfelelően magas színvonalon berendezettek: márvány\r\nfürdőszoba, síkképernyős tv és szélessávú internet is biztosított.<br>\r\n\r\n<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 13 600 Ft/fő/éjtől', 28, ''),
(14, 'Radisson Blu Hotel Berlin *****', 5, '<p><span style="Times New Roman&quot;;Times New Roman&quot;;\r\ncolor:#343434;"><br>\r\n\r\nA főváros szívében, a Spree-folyó partján helyezkedik el a hotel, a\r\nkatedrálissal és a Múzeumszigettel szemközt. Ízlésesen berendezett szobái\r\nminden kényelemmel felszereltek: többek közt szélessávú internettel, LCD\r\ntv-vel, széffel, minibárral. A szálloda különlegessége az átriumban található\r\nhenger alakú tengervizes akvárium számtalan színes hallal és élőlénnyel. Egy\r\nkétemeletes üvegfalú lift van a közepén, hogy jobban megfigyelhesse a\r\nkülönleges élővilágot. A spaban fitneszterem, medence, többféle szauna és\r\nmasszázs kényezteti a vendégeket.<br>\r\n\r\n<br>\r\n\r\n<span id="pastemarkerend">&nbsp;</span><br>\r\n\r\n</span></p>\r\n', '', 'Kétágyas szoba 20 000 Ft/fő/éjtől. Vásárok, kiállítások és egyéb kiemelt rendezvények alatt az árak nem biztos, hogy érvényesek. Erre vonatkozóan kérjük kérje pontos ajánlatunkat!', 28, '');

