INSERT INTO `countries` (`id`, `information`, `useful_information`, `created`, `name`, `visa_info`, `image_file`, `slug`, `keywords`, `title`, `continent_id`, `interesting_information`) VALUES
(1, 'Görögország', 'Görögország hasznos', '2012-12-21 12:27:57', 'Görögország', 'vízum információ: Görögország', 'gorogo.png', 'Görögország1', 'Görögország', 'Görögország', 1, ''),
(2, 'Deviza előírások: a belföldi és külföldi fizetőeszközök be- és kivitele nem korlátozott.\r\nEgészségügyi előírások: kötelező védőoltás nincs, több helyen a palackozott víz fogyasztása javasolt.\r\nElektromos feszültség: 220 V.\r\nPénznem: euro, 1 EUR = 100 cent. Nemzetközi hitelkártyák széles körben elfogadottak.\r\nIdőjárás: kontinentális', 'Franciaország nemzeti szimbóluma Marianne, a gall-sapkás nőalak, aki egyben a szabadság és a francia forradalom jelképe is. Az előző szimbólum a Gall Kakas volt, a vakmerő bátorság jelképe.\r\n\r\n<p>Szomszédai Belgium, Luxemburg, Németország, Svájc, Olaszország, Monaco, Andorra és Spanyolország. Tengerentúli függőségein keresztül határos Brazíliával, Suriname-mal és a Holland Antillákkal. Franciaországot és Nagy-Britanniát a La Manche-csatorna alatt futó Csalagút köti össze. Franciaország a legnagyobb területű nyugat-európai ország és a második legnagyobb kizárólagos gazdasági övezettel rendelkezik, mely több mint 11 millió négyzetkilométer területű.</p>', '2013-01-02 10:51:11', 'Franciaország', 'vízum információ: Franciaország', 'franciao.png', 'franciaorszag', 'Franciaország', 'Franciaország', 1, ''),
(3, 'országinformáció: Spanyolország', 'A spanyol szó végső eredete a latin HISPANIOLUS, azaz kb. ’hispánka’, amely espaignol alakban került a provanszálba, onnan español írásmóddal a spanyolba, majd a többi (újlatin) nyelvbe. A magyar nyelvben egy északolasz nyelvjárási – s-sel ejtett – spagnol alak honosodott meg.', '2013-01-02 10:51:19', 'Spanyolország', 'vízum információ: Spanyolország', 'franciao.png', 'Spanyolország3', 'Spanyolország', 'Spanyolország', 1, ''),
(4, 'országinformáció: Skócia', 'Skócia (angolul Scotland, skót gaelül Alba) Nyugat-Európában található, Nagy-Britannia második legnagyobb országrésze terület és népesség alapján. A Brit-sziget északi harmadát foglalja el, délről Anglia, keletről az Északi-tenger, északról és nyugatról az Atlanti-óceán határolja, délnyugatról pedig az Északi-csatorna és az Ír-tenger. Mintegy 790 sziget tartozik hozzá.', '2013-01-02 10:51:26', 'Skócia', 'vízum információ: Skócia', '3.jpeg', 'Skócia4', 'Skócia', 'Skócia', 1, ''),
